import View from './View';

import ObjectView from './ObjectView';


/**
 * View for Array model
 */
export default  class ArrayView extends View {

    constructor(element) {

        super( element ).name = element.dataset.array;

        this.template = element.firstElementChild;

        element.innerHTML = '';
    }

    /**
     * @param {Iterable} list
     *
     * @return {ArrayView}
     */
    render(list) {

        list = Array.from(
            list,
            item  =>
                (new ObjectView( this.template.cloneNode(true) )).render( item )
        );

        this.push(... list);

        this.element.append(... list.map(view => view.element));

        return this;
    }
}

import View from './View';

import { mapTree } from './utility';

import Template from './Template';

import ArrayView from './ArrayView';

const template_element = new WeakMap();


/**
 * View for Object model
 */
export default  class ObjectView extends View {

    constructor(element) {

        super( element ).name = element.dataset.object;

        this.scan();
    }

    /**
     * @private
     */
    static templateOf(node, renderer) {

        return  new Template(node.value || node.nodeValue,  ['view'],  renderer);
    }

    /**
     * @private
     */
    register(element, template) {

        if (element instanceof View)
            this[ this.length ] = element;
        else if ( template[0] )
            template_element.set(this[ this.length ] = template,  element);
    }

    /**
     * @private
     */
    parseTag(element) {

        for (let attr of element.attributes) {

            let name = attr.name;

            let template = ObjectView.templateOf(
                attr,
                (name in element)  ?
                    (value  =>  element[ name ] = value)  :
                    (value  =>  element.setAttribute(name, value))
            );

            if (template == '')  element.removeAttribute( name );

            this.register(element, template);
        }
    }

    /**
     * @private
     */
    scan() {

        var root = this.element;

        root = root.parentNode  ?  root  :  {childNodes: [ root ]};

        mapTree(root,  'childNodes',  (node) => {

            switch ( node.nodeType ) {
                case 1:
                    if ( node.dataset.object )
                        this.register(new ObjectView( node ));
                    else if ( node.dataset.array )
                        this.register(new ArrayView( node ));
                    else
                        this.parseTag( node );
                    break;
                case 3:
                    this.register(
                        node,
                        ObjectView.templateOf(
                            node,  value => node.nodeValue = value
                        )
                    );
            }

            return node;
        });
    }

    /**
     * @param {Object} data
     *
     * @return {ObjectView}
     */
    render(data) {

        for (let template of this)
            if (template instanceof Template)
                template.evaluate(template_element.get( template ),  data);
            else if (template instanceof View)
                template.render( data[ template.name ] );

        return this;
    }
}

/**
 * String template
 */
export default  class Template extends Array {
    /**
     * @param {string}          raw
     * @param {stirng[]}        [varName]  - Name list of the Local variable
     * @param {ChangedCallback} [onChange] - Call with New & Old value
     * @param {Array}           [bindData] - The parameter bound to `onChange`
     */
    constructor(raw, varName, onChange, bindData) {

        super();

        this.raw = raw,  this.value = null;

        if (varName instanceof Function)
            bindData = onChange, onChange = varName, varName = null;

        this.varName = varName || [ ],  this.expression = [ ];

        this.context = new Set( this.varName.concat('this') );

        this.onChange = (onChange instanceof Function)  ?  onChange  :  null;

        this.data = bindData || [ ];

        this.parse().clear();
    }

    static get Expression() {  return /\$\{([\s\S]+?)\}/g;  }

    static get Reference() {  return /(\w+)(?:\.(\w+)|\[(?:'([^']+)|"([^"]+)))/g;  }

    /**
     * @private
     */
    compile(expression) {

        return this.expression.push(
            new Function(... this.varName,  'return ' + expression.trim())
        );
    }

    /**
     * @private
     */
    parse() {

        const addReference = (match, context, key1, key2, key3)  =>  {

            if (this.context.has( context ))  this.push(key1 || key2 || key3);
        };

        this.raw = this.raw.replace(
            Template.Expression,  (_, expression) => {

                expression.replace(Template.Reference, addReference);

                return  '${' + (this.compile( expression ) - 1) + '}';
            }
        );

        return this;
    }

    /**
     * Evaluate expression
     *
     * @param {?object} context     - Value of `this` in the expression
     * @param {...*}    [parameter] - One or more value of the Local variable
     *
     * @return {string}
     */
    evaluate(context, ...parameter) {

        const value = this.raw.replace(
            /\$\{(\d+)\}/g,
            (_, index)  =>  this.expression[ index ].apply(context, parameter)
        );

        if (value !== this.value) {
            /**
             * Call back only on Value changed
             *
             * @typedef {function} ChangedCallback
             *
             * @param {*}    newValue
             * @param {*}    oldValue
             * @param {...*} bindData
             */
            if ( this.onChange )
                this.onChange(... [value, this.value].concat( this.data ));

            this.value = value;
        }

        return value;
    }

    clear() {

        const data = { };

        for (let key of this)  data[ key ] = '';

        return  this.evaluate(... Array.from(this.context,  () => data));
    }

    toString() {

        return  this.value + '';
    }
}

var depth = 0;

/**
 * Traverse Object-tree & return Node array through the filter
 *
 * @param {object}        node     - Object tree
 * @param {string}        fork_key - Key of children list
 * @param {MapTreeFilter} filter   - Map filter
 *
 * @return {Array}  Result list of Map filter
 */
export function mapTree(node, fork_key, filter) {

    var children = node[fork_key], list = [ ];    depth++ ;

    for (var i = 0, value;  i < children.length;  i++) {
        /**
         * @typedef {function} MapTreeFilter
         *
         * @param {object} child
         * @param {number} index
         * @param {number} depth
         *
         * @return {?object}  `Null` or `Undefined` to **Skip the Sub-Tree**,
         *                    and Any other Type to Add into the Result Array.
         */
        try {
            value = filter.call(node, children[i], i, depth);

        } catch (error) {

            depth = 0;    throw error;
        }

        if (! (value != null))  continue;

        list.push( value );

        if ((children[i] != null)  &&  (children[i][fork_key] || '')[0])
            list.push.apply(
                list,  mapTree(children[i], fork_key, filter)
            );
    }

    depth-- ;

    return list;
}

const view_element = new WeakMap();


/**
 * Abstract View
 */
export default  class View extends Array {
    /**
     * @param {Element} element
     */
    constructor(element) {

        view_element.set(super(), element);

        if (this.constructor === View)
            throw TypeError('"View" is an abstract class');
    }

    /**
     * @return {Element}
     */
    get element() {

        return  view_element.get( this );
    }

    /**
     * Render this view
     *
     * @return {View}
     */
    render() {

        throw TypeError('View.prototype.render() must be overwriten');
    }
}

import { resolve } from 'path';

import { readFileSync } from 'fs';

import { JSDOM } from 'jsdom';

import ArrayView from '../../source/ArrayView';

import ObjectView from '../../source/ObjectView';

var HTML;


/**
 * @test {ArrayView}
 */
describe('ArrayView()',  () => {

    before(()  =>  HTML = readFileSync( resolve(module.id, '../index.html') ));

    describe('Single view',  () => {

        var view, element;

        before(() => {

            element = JSDOM.fragment( HTML ).querySelector('ol');

            element.remove();
        });

        /**
         * @test {ArrayView#constructor}
         */
        it('Scan DOM',  () => {

            view = new ArrayView( element );

            view.element.textContent.should.be.equal('');
        });

        /**
         * @test {ArrayView#render}
         */
        it('Render data',  () => {

            view.render([
                {name: 'ObjectView'},
                {name: 'ArrayView'},
                {name: 'TreeView'}
            ]);

            Array.from(
                view.element.children,  item => item.textContent
            ).should.be.eql([
                'ObjectView', 'ArrayView', 'TreeView'
            ]);
        });
    });

    describe('Nested view',  () => {

        var view, element;

        before(() => {

            element = JSDOM.fragment( HTML ).children[0];

            element.remove();
        });

        /**
         * @test {ObjectView#scan}
         */
        it('Scan DOM',  () => {

            view = new ObjectView( element );

            view.length.should.be.equal( 2 );

            view[1].should.be.instanceof( ArrayView );
        });

        /**
         * @test {ObjectView#render}
         */
        it('Render data',  () => {

            const list = [
                {name: 'Gecko'},
                {name: 'Trident'},
                {name: 'WebKit'},
                {name: 'Blink'},
                {name: 'EdgeHTML'}
            ];

            view.render({
                title:  'Kinds of Web browser core',
                list:   list
            });

            view.element.outerHTML.should.be.equal(`
<main>
    <h1>Kinds of Web browser core</h1>

    <ol data-array="list">${
    list.map(item => `<li>${item.name}</li>`).join('')
}</ol>
</main>`.trim()
            );
        });
    });
});

