import Component, {_private_} from './Component';

import { extend } from './utility';


/**
 * Register a component
 *
 * @param {function} subClass
 *
 * @return {function} A proxy class of `subClass`
 */
export function component(subClass) {

    var {template, style} = Component.findTemplate();

    template = template.content;

    if ( style[0] )  template.prepend(... style);


    subClass = new Proxy(
        extend(subClass, Component),  {
            construct(target, argument, newTarget) {

                const _this_ = Reflect.construct(target, argument, newTarget);

                _private_.set(_this_,  { });

                _this_.attachShadow({
                    mode:              'open',
                    delegatesFocus:    true
                }).appendChild(
                    template.cloneNode( true )
                );

                return _this_;
            }
        }
    );

    customElements.define(subClass.tagName, subClass);

    return subClass;
}


/**
 * Set private data of an HTMLElement
 *
 * @param {Element} element
 * @param {string}  key
 * @param {*}       value
 *
 * @return {*} The `value` parameter
 */
export function set(element, key, value) {

    return _private_.get( element )[ key ] = value;
}


/**
 * Get private data of an HTMLElement
 *
 * @param {Element} element
 * @param {string}  key
 *
 * @return {*}
 */
export function get(element, key) {

    return _private_.get( element )[ key ];
}

import View from './View';

import { mapTree } from './utility';

import Template from './Template';

import ArrayView from './ArrayView';

const template_element = new WeakMap();


/**
 * View for Object model
 */
export default  class ObjectView extends View {
    /**
     * @param {string|Element|DocumentFragment} template
     */
    constructor(template) {

        super(template,  'object',  { }).scan();
    }

    valueOf() {

        const data = Object.assign({ },  this.data);

        for (let template of this)
            if (template instanceof View)
                data[ template.name ] = template.valueOf();

        return data;
    }

    /**
     * @private
     *
     * @param {Node|Attr} node
     * @param {function}  renderer
     *
     * @return {Template}
     */
    static templateOf(node, renderer) {

        return  new Template(node.value || node.nodeValue,  ['view'],  renderer);
    }

    /**
     * @private
     *
     * @param {Element|View} element
     * @param {Template}     [template]
     */
    register(element, template) {

        if (element instanceof View)
            this[ this.length ] = element;
        else if ( template[0] )
            template_element.set(this[ this.length ] = template,  element);
    }

    /**
     * @private
     *
     * @param {Element} element
     */
    parseTag(element) {

        for (let attr of element.attributes) {

            let name = attr.name;

            let template = ObjectView.templateOf(
                attr,
                (name in element)  ?
                    (value  =>  element[ name ] = value)  :
                    (value  =>  element.setAttribute(name, value))
            );

            if (template == '')  element.removeAttribute( name );

            this.register(element, template);
        }
    }

    /**
     * @private
     */
    scan() {

        var root = this.content;

        root = root.parentNode ? root : {
            childNodes:    (root instanceof Array)  ?  root  :  [ root ]
        };

        mapTree(root,  'childNodes',  (node) => {

            switch ( node.nodeType ) {
                case 1:
                    if ( node.dataset.object )
                        this.register(new ObjectView( node ));
                    else if ( node.dataset.array )
                        this.register(new ArrayView( node ));
                    else
                        this.parseTag( node );
                    break;
                case 3:
                    this.register(
                        node,
                        ObjectView.templateOf(
                            node,  value => node.nodeValue = value
                        )
                    );
            }

            return node;
        });
    }

    /**
     * @param {Object} data
     *
     * @return {ObjectView}
     */
    render(data) {

        const _data_ = Object.assign(this.data, data);

        for (let template of this) {

            let name = template.name;

            if (template instanceof Template)
                template.evaluate(template_element.get( template ),  _data_);
            else if (
                (template instanceof View)  &&  data[ name ]
            )
                _data_[name] = template.render( data[ name ] ).data;
        }

        return this;
    }

    clear() {

        for (let template of this)  template.clear();

        return this;
    }
}

/**
 * String template
 */
export default  class Template extends Array {
    /**
     * @param {string}          raw
     * @param {stirng[]}        [varName]  - Name list of the Local variable
     * @param {ChangedCallback} [onChange] - Call with New & Old value
     * @param {Array}           [bindData] - The parameter bound to `onChange`
     */
    constructor(raw, varName, onChange, bindData) {

        super();

        this.raw = raw,  this.value = null;

        if (varName instanceof Function)
            bindData = onChange, onChange = varName, varName = null;

        this.varName = varName || [ ],  this.expression = [ ];

        this.context = new Set( this.varName.concat('this') );

        this.onChange = (onChange instanceof Function)  ?  onChange  :  null;

        this.data = bindData || [ ];

        this.parse().clear();
    }

    static get Expression() {  return /\$\{([\s\S]+?)\}/g;  }

    static get Reference() {  return /(\w+)(?:\.(\w+)|\[(?:'([^']+)|"([^"]+)))/g;  }

    /**
     * @private
     */
    compile(expression) {

        return this.expression.push(
            new Function(... this.varName,  'return ' + expression.trim())
        );
    }

    /**
     * @private
     */
    parse() {

        const addReference = (match, context, key1, key2, key3)  =>  {

            if (this.context.has( context ))  this.push(key1 || key2 || key3);
        };

        this.raw = this.raw.replace(
            Template.Expression,  (_, expression) => {

                expression.replace(Template.Reference, addReference);

                return  '${' + (this.compile( expression ) - 1) + '}';
            }
        );

        return this;
    }

    /**
     * Evaluate expression
     *
     * @param {?object} context     - Value of `this` in the expression
     * @param {...*}    [parameter] - One or more value of the Local variable
     *
     * @return {string}
     */
    evaluate(context, ...parameter) {

        const value = this.raw.replace(
            /\$\{(\d+)\}/g,
            (_, index)  =>  this.expression[ index ].apply(context, parameter)
        );

        if (value !== this.value) {
            /**
             * Call back only on Value changed
             *
             * @typedef {function} ChangedCallback
             *
             * @param {*}    newValue
             * @param {*}    oldValue
             * @param {...*} bindData
             */
            if ( this.onChange )
                this.onChange(... [value, this.value].concat( this.data ));

            this.value = value;
        }

        return value;
    }

    clear() {

        const data = { };

        for (let key of this)  data[ key ] = '';

        return  this.evaluate(... Array.from(this.context,  () => data));
    }

    toString() {

        return  this.value + '';
    }
}

/**
 * Merge own properties of two or more objects together into the first object
 * by their descriptor
 *
 * @param {Object}    target - An object that will receive the new properties
 *                             if `source` are passed in
 * @param {...Object} source - Additional objects containing properties to merge in
 *
 * @return {Object} The `target` parameter
 */
export function extend(target, ...source) {

    for (let object of source)  if (object instanceof Object) {

        let descriptor = Object.getOwnPropertyDescriptors( object );

        if (target instanceof Function) {

            delete descriptor.name;
            delete descriptor.length;
            delete descriptor.prototype;

            Object.defineProperties(
                target.prototype,
                Object.getOwnPropertyDescriptors( object.prototype )
            );
        }

        Object.defineProperties(target, descriptor);
    }

    return target;
}


var depth = 0;

/**
 * Traverse Object-tree & return Node array through the filter
 *
 * @param {object}        node     - Object tree
 * @param {string}        fork_key - Key of children list
 * @param {MapTreeFilter} filter   - Map filter
 *
 * @return {Array}  Result list of Map filter
 */
export function mapTree(node, fork_key, filter) {

    var children = node[fork_key], list = [ ];    depth++ ;

    for (var i = 0, value;  i < children.length;  i++) {
        /**
         * @typedef {function} MapTreeFilter
         *
         * @param {object} child
         * @param {number} index
         * @param {number} depth
         *
         * @return {?object}  `Null` or `Undefined` to **Skip the Sub-Tree**,
         *                    and Any other Type to Add into the Result Array.
         */
        try {
            value = filter.call(node, children[i], i, depth);

        } catch (error) {

            depth = 0;    throw error;
        }

        if (! (value != null))  continue;

        list.push( value );

        if ((children[i] != null)  &&  (children[i][fork_key] || '')[0])
            list.push.apply(
                list,  mapTree(children[i], fork_key, filter)
            );
    }

    depth-- ;

    return list;
}

const view_DOM = new WeakMap(), DOM_view = new WeakMap(), view_data = new WeakMap();


/**
 * Abstract View
 */
export default  class View extends Array {
    /**
     * @param {string|Element|DocumentFragment} template
     * @param {string}                          nameKey  - Key (littleCamelCase) of HTML `data-*`
     *                                                     to get name of bound data
     * @param {Object}                          scope    - Empty Model for this view
     */
    constructor(template, nameKey, scope) {

        if (super().constructor === View)
            throw TypeError('"View" is an abstract class');

        switch ( template.nodeType ) {
            case 1:
                this.name = template.dataset[ nameKey ];  break;
            case 11:
                if (! template.parentNode)
                    template = Array.from( template.childNodes );
                break;
            default:
                template = View.parseDOM( template );
        }

        view_data.set(this.bindWith( template ),  scope);
    }

    /**
     * @param {Node} node - Node from other `Document`
     *
     * @return {Node} Cloned & imported Node (with its child nodes)
     */
    static import(node) {  return  document.importNode(node, true);  }

    /**
     * @param {string} markup - Code of an markup fragment
     *
     * @return {Element[]}
     */
    static parseDOM(markup) {

        markup = (new window.DOMParser()).parseFromString(markup, 'text/html');

        return  Array.from(markup.head.childNodes, View.import).concat(
            Array.from(markup.body.childNodes, View.import)
        );
    }

    /**
     * @protected
     *
     * @param {Element|Element[]|DocumentFragment} template
     *
     * @return {View}
     */
    bindWith(template) {

        view_DOM.set(this, template);

        if (template instanceof Array) {

            for (let node of template)
                if (node.nodeType === 1)  DOM_view.set(node, this);
        } else
            DOM_view.set(template, this);

        return this;
    }

    /**
     * @param {Element|DocumentFragment} node
     *
     * @return {View} View instance bound with `node`
     */
    static instanceOf(node) {  return  DOM_view.get( node );  }

    /**
     * @return {Element|Element[]|DocumentFragment}
     */
    get content() {  return  view_DOM.get( this );  }

    /**
     * @return {string} Full markup code of this view
     */
    toString() {

        const content = this.content;

        return  (content.nodeType === 1)  ?
            content.outerHTML  :
            Array.from(content.childNodes || content,  node => {

                switch ( node.nodeType ) {
                    case 1:    return node.outerHTML;
                    case 3:    return node.nodeValue;
                }
            }).join('');
    }

    /**
     * @protected
     *
     * @type {Object}
     */
    get data() {  return  view_data.get( this );  }

    /**
     * Get original data of this view
     *
     * @return {Object}
     */
    valueOf() {

        throw TypeError('View.prototype.valueOf() must be overwriten');
    }

    /**
     * Render this view
     *
     * @return {View}
     */
    render() {

        throw TypeError('View.prototype.render() must be overwriten');
    }

    /**
     * Reset this view to empty data
     *
     * @return {View}
     */
    clear() {

        throw TypeError('View.prototype.clear() must be overwriten');
    }
}

