    });

    /**
     * @test {Template#onChange}
     */
    it('Changed callback',  () => {

        const empty = `[ ${(new Date()).getFullYear()} ]  Hello,  !`;

        const template = new Template(
            '[ ${(new Date()).getFullYear()} ]  Hello, ${this.name} !',
            (newValue, oldValue, ...bindData)  =>  {

                if (oldValue === null)
                    newValue.should.be.equal( empty );
                else {
                    oldValue.should.be.equal( empty );

                    newValue.should.be.equal(
                        `[ ${(new Date()).getFullYear()} ]  Hello, EWA !`
                    );
                }

                bindData.should.be.eql([1, 2, 3]);
            },
            [1, 2, 3]
        );

        template.evaluate({name: 'EWA'});
    });
});

import { extend, mapTree } from '../source/utility';


describe('Utility',  () => {
    /**
     * @test {extend}
     */
    describe('Mixin objects',  () => {

        it('Ordinary objects',  () => {

            extend(
                { },  null,  {example: 1, get test() { }}
            ).should.have.properties(
                'example', 'test'
            );
        });

        it('Function or Class',  () => {

            function Test() { }

            class Example {
                constructor(test) {  test;  }

                static test() { }

                test() { }
            }

            extend(Test, Example).should.have.properties({
                name:    'Test',
                length:  0,
                test:    Example.test
            });

            Test.prototype.should.not.be.equal( Example );

            Test.prototype.should.have.property('test', Example.prototype.test);
        });
    });

    /**
     *  @test {mapTree}
     */
    it('Filter object tree',  () => {

        mapTree({
            id:        0,
            children:  [
                {
                    id:        1,
                    children:  [
                        {
                            id:        3,
                            children:  []
                        }
                    ]
                },
                {
                    id:        2,
                    children:  []
                }
            ]
        },  'children',  function (child, index, depth) {

            if (depth < 2)  return child.id;

        }).should.be.eql([1, 2]);
    });
});
